  ctx.save();
  this.set_zoom_transparency(ctx);
  for(var i=0; i < this.buttons.length; i++) {
    this.buttons[i].draw(ctx)
  }
  ctx.restore();

  ctx.save();
  ctx.translate(constants.sideBarWidth, 0)//allows us to have a topbar
  this.set_zoom_transparency(ctx);
